import React, { useState } from 'react';
import SearchBar from '../Components/SearchBar';

function SearchJobs() {
  const [jobs, setJobs] = useState([]);

  const fetchJobs = async (jobTitle, location) => {
    const url = `https://indeed12.p.rapidapi.com/jobs/search?query=${jobTitle}&location=${location}&page_id=1&locality=us&fromage=1&radius=25`;

    // Assuming you have the necessary headers for the RapidAPI request
    const options = {
      method: 'GET',
      headers: {
        'X-RapidAPI-Key': 'your-rapidapi-key',
        'X-RapidAPI-Host': 'indeed12.p.rapidapi.com'
      }
    };

    try {
      const response = await fetch(url, options);
      const data = await response.json();
      setJobs(data.jobs); // Assuming the response contains a `jobs` array
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  return (
    <div>
      <SearchBar onSearch={(searchTitle) => fetchJobs(searchTitle, 'chicago')} />
      {/* Render jobs here */}
      <div>
        {jobs.map((job, index) => (
          <div key={index}>
            <h3>{job.title}</h3>
            <p>{job.company}</p>
            <p>{job.location}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SearchJobs;
