import React, { useState } from 'react';
import SearchCompanies from '../Components/SearchCompanies';

function SearchCompaniesPage() {
  const [companyName, setCompanyName] = useState('');
  const [companyInfo, setCompanyInfo] = useState([]);

  const fetchCompanyJobs = async (company) => {
    const url = `https://indeed12.p.rapidapi.com/company/${company}/jobs?locality=us&start=1`;
    const options = {
      method: 'GET',
      headers: {
        'x-rapidapi-key': process.env.REACT_APP_API_KEY,
        'x-rapidapi-host': 'indeed12.p.rapidapi.com'
      }
    };

    try {
      const response = await fetch(url, options);
      const result = await response.json();
      console.log(result);

      setCompanyInfo(result);
    } catch (error) {
      console.error(error);
    }
  };

  const handleSearch = (searchTerm) => {
    setCompanyName(searchTerm);
    fetchCompanyJobs(searchTerm);
  };

  return (
    <div>
      <SearchCompanies onSearch={handleSearch} />
      <div className='flex items-center justify-center flex-wrap'>
        <div>
            {companyInfo.length > 0 ?(
              companyInfo.map(jobs=>(
                <a key={jobs.id} href={jobs.final_url} className='w-full md:w-[480px]'>
                  <div className='border bg-white shadow-lg px-5 py-5 flex items-center md:h-[170px]'>
                      <div className='w-[60px] h-[60px] rounded-full border flex-shrink-0'>
                          {/* Company logo goes here */}
                      </div>
                      <div className='ml-5 flex-grow'>
                          <span className='block font-bold text-lg'>{jobs.titile}</span>
                          <p className='text-sm text-gray-500 my-3'>
                              <span>company</span>. <span>{jobs.location}</span>
                          </p>
                          <span className='inline-block px-2 py-1 rounded-full text-[#56CDAD] bg-[#56CDAD] bg-opacity-20 text-sm'>
                              Full Time
                          </span>
                      </div>
                  </div>
                </a>
              ))

            ):(
                <p className='mt-4 text-center'>No Positions Were found from this Company...</p>
            )}
        </div>
      </div>
    </div>
  );
}

export default SearchCompaniesPage;
