import { React, useEffect, useState } from 'react'

function LatestJobs() {



    //create useState to change data in featuredItems cards
    const [data, setData] = useState([])
    //fetch featured jobs from rapid api api
    const fetchLatestJobs = async ()=>{
        const url = "https://indeed12.p.rapidapi.com/jobs/search?query=manager&location=Georgia&page_id=1&locality=us&fromage=1&radius=";
        const options = {
            method: 'GET',
            headers: {
                'x-rapidapi-key':process.env.REACT_APP_API_KEY,
                'x-rapidapi-host' : 'indeed12.p.rapidapi.com'
            }
        }

        try{
            const response = await fetch(url, options);
            if(!response.ok) {
                throw new Error(`HTTP request failed! Error: ${response.status}`);
            }
            const result = await response.json();
            //slice result to 8
            const modifiedResult = result.hits.slice(0,8);
            setData(modifiedResult);




        } catch(error) {
            console.error(`Failed To Fetch Latest Jobs From Indeed Rapid Api`)
        };
    }



    useEffect(()=>{
        fetchLatestJobs();
    }, []);

  return (
    <div className='py-20 bg-[#F8F8FD] flex justify-center items-center'>
        <div className='flex flex-wrap justify-center gap-[50px]'>
            {data.map(job =>(
                <a key={job.id} href={job.link}>
                    <div className='max-w-[480px]  md:w-[480px]h-[170px] bordercbg-white shadow-lg px-5 py-5'>
                        <div>
                            <div>
                                <div className='w-[60px] h-[60px] rounded-full border float-left'>
                                    {/***company logo goes here */}
                                </div>
                                <span className='ml-7'>{job.title}</span>
                                <p className='text-[14px] text-gray-500 ml-[86px] my-3'><span className='text-center'>{job.company_name}</span>. <span>{job.location} <span></span></span></p>
                                <span className='rounded-full px-[7px] py-1 ml-[86px] text-[#56CDAD] bg-[#56CDAD] bg-opacity-20 '>Full Time</span>
                            </div>
                        </div>
                    </div>
                </a>

            ))}
        </div>
    </div>
  )
}

export default LatestJobs
