import { React, useEffect, useState } from 'react'

function FeaturedJobs() {

    //create useState to change data in featuredItems cards
    const [data, setData] = useState([])
    //fetch featured jobs from rapid api api
    const fetchFeaturedJobs = async ()=>{
        const url = "https://indeed12.p.rapidapi.com/jobs/search?query=manager&location=Georgia&page_id=1&locality=us&fromage=1&radius=";
        const options = {
            method: 'GET',
            headers: {
                'x-rapidapi-key':process.env.REACT_APP_API_KEY,
                'x-rapidapi-host' : 'indeed12.p.rapidapi.com'
            }
        }

        try{
            const response = await fetch(url, options);
            if(!response.ok) {
                throw new Error(`HTTP request failed! Error: ${response.status}`);
            }
            const result = await response.json();
            //slice result to 8
            const modifiedResult = result.hits.slice(0, 8);
            setData(modifiedResult);




        } catch(error) {
            console.error(`Failed To Fetch Featured Jobs From Indeed Rapid Api`)
        };
    }


    const fetchLogo = async(company_name) => {
        const url = `https://indeed12.p.rapidapi.com/company/Microsoft?locality=us`
    }




    useEffect(()=>{
        fetchFeaturedJobs();
    }, []);

  return (
    <div className='py-6'>
        <div className='flex items-center justify-center flex-wrap gap-[30px] px-[100px]'>
            {data.length > 0 ? (
                data.map(content=>(
                    <a key={content.id} href={content.link}>
                        <div className='w-[250px] border px-4 py-4  hover:scale-[1.1] hover:shadow-custom-dark transition-all duration-[.5s]'>
                            <div className='flex justify-between'>
                                <div className='w-[50px] h-[50px] rounded-full border'>
                                    {/**company Logo Icon */}
                                </div>
                                <p className='border border-purple-200 px-2 h-[35px] flex items-center text-center'>Full Time</p>
                            </div>
                            <div className='mt-6 py-2'>
                                <p className='font-[600]'>{content.title}</p>
                                <span className='text-[12px] text-gray-500'>{content.company_name} <span>.</span> <span>{content.location}</span></span>
                            </div>
                            <div className=''>
                                <span className='text-[13px]'>Revolut is looking for Email Marketing to help team ma ...</span>
                            </div>
                        </div>
                    </a>
                ))

            ):(
                <p>Loading...</p>
            )}

        </div>

    </div>
  )
}

export default FeaturedJobs
